    function loadCart()
    {
        document.getElementById('order-loader').className = "btn-light"
        document.getElementById('cart-loader').className = "btn-primary"
        let cart_container = document.getElementById('cartcontainer');
        let xhr = new XMLHttpRequest()

        xhr.onreadystatechange = function()
        {

            if(this.status==200 && this.readyState==4)
            {
                cart_container.innerHTML = this.responseText
                // console.log(this.responseText)
            }
        }
        xhr.open('GET','../core/api/cart.php?action=my_cart',true)
        xhr.send() // Sends Request
    }

    function loadOrders()
    {
        document.getElementById('cart-loader').className = "btn-light"
        document.getElementById('order-loader').className = "btn-primary"
        let cart_container = document.getElementById('cartcontainer');
        let xhr = new XMLHttpRequest()

        xhr.onreadystatechange = function()
        {
            if(this.status==200 && this.readyState==4)
            {
                cart_container.innerHTML = this.responseText
                // console.log(this.responseText)
            }
        }

        xhr.open('GET','../core/api/cart.php?action=my_orders',true)
        xhr.send() // Sends Request
    }

    function deleteCart(id)
    {
        id = document.getElementById('btn-del-cr').name
        let url = '../core/api/cart.php?action=delete_cart&cart_id='+id
        let xhr = new XMLHttpRequest()
        xhr.open('GET',url,true)
        xhr.onload = ()=>{
            loadCart()
        }

        xhr.send() // Sends Request
    }

    function deleteOrder(id)
    {
        id = document.getElementById('btn-del-ord').name
        let url = '../core/api/cart.php?action=delete_order&order_id='+id
        let xhr = new XMLHttpRequest()
        xhr.open('GET',url,true)
        xhr.onload = ()=>
        {
            loadOrders()
        }
        xhr.send() // Sends Request
    }
